const headerElement=document.getElementsByClassName("header")[0],menu__list_linkElement=document.getElementsByClassName("menu__list-link"),instagramElement=document.getElementsByClassName("instagram")[0];addEventListener("scroll",(e=>{if(window.scrollY>0){headerElement.classList.add("header--scrolled");for(let e of menu__list_linkElement)e.classList.add("menu__list-link--scrolled");instagramElement.classList.add("instagram--scrolled")}else{headerElement.classList.remove("header--scrolled");for(let e of menu__list_linkElement)e.classList.remove("menu__list-link--scrolled");instagramElement.classList.remove("instagram--scrolled")}}));